	def gen_num(self,keyLenght=1024, group=5):
	
		iniciopKp=time.time()
		self.p=2
		self.q=2
		self.r=self.getPrime(group)
		self.n= self.p*self.q*self.r;
		self.phi=2*self.r-2;
		finpKp=time.time()

		#print("Valor de p:\n{}\n".format(self.p))
