	def gcd(self,a, b):
		while b != 0:
			a, b = b, a % b
		return a

	def getModInverse(self,a, m):
		if math.gcd(a, m) != 1:
			return None
		u1, u2, u3 = 1, 0, a
		v1, v2, v3 = 0, 1, m

		while v3 != 0:
			q = u3 // v3
			v1, v2, v3, u1, u2, u3 = (
			u1 - q * v1), (u2 - q * v2), (u3 - q * v3), v1, v2, v3
		return u1 % m

	def mulmod(self,a, b, mod):
	
		res = 0; 
		a = a % mod; 
		while (b > 0): 
			if (b % 2 == 1): 
				res = (res + a) % mod;
			a = (a * 2) % mod; 
			b //= 2;
		return res % mod; 
    
	def genPrivKeys(self,insert=0):

		tamanio=int(self.keyLength)//2
		iniciopKp=time.time()
		self.X = getrandbits(tamanio)
		self.Y = self.phi+1-self.X
		self.K = random.randrange(1, self.n)
		g = self.gcd(self.K, self.n)
		while g != 1:
			self.K = random.randrange(1, self.n)
			g = self.gcd(self.K, self.n)		
		self.KInv = self.getModInverse(self.K, self.n)
		finpKp=time.time()

		print('El tiempo de generación de Llaves Privadas es:',(finpKp-iniciopKp), 'segundos')



	def genPublicKeys(self,insert=0):

		iniciopKp=time.time()
		temp1=pow(self.p,2*self.X,self.n)
		temp2=pow(self.q,self.Y,self.n)
		self.pp=self.mulmod(temp1, self.K, self.n)
		self.pq=self.mulmod(temp2, self.K, self.n)
		finpKp=time.time()
		#print("Valor de pp:\n{}\n".format(self.pp))
		#print("Valor de pq:\n{}\n".format(self.pq))
		print('El tiempo de generación de Llaves Públicas es:',(finpKp-iniciopKp), 'segundos')	



	def genShared(self,_pp,_pq):
		
		t1=pow(_pp,self.X,self.n)
		t2=pow(_pq,self.Y,self.n)
		
		return self.mulmod(t1,t2,self.n)
		

	def getShared(self,pre):
		
		self.Ks=self.mulmod(pre,self.KInv,self.n)
		return self.Ks

	def preparecipher(self):

		self.Kr= self.Ks//(self.p*self.q) % self.r
		self.KrInv = self.getModInverse(int(self.Kr), self.r)
	
	def cipherNumber(self,number):
		
		iniciopKp=time.time()
		x=self.mulmod(int(number), self.Kr, self.r)
		finpKp=time.time()
		return x
	
	def decipherNumber(self,number):
		iniciopKp=time.time()
		x=self.mulmod(number, self.KrInv, self.r)
		finpKp=time.time()
		return x		
